#pragma config(Sensor, S1,     Rbat,           sensorEV3_Ultrasonic)
#pragma config(Sensor, S3,     Fbat,           sensorEV3_Ultrasonic)
#pragma config(Motor,  motorB,          left,          tmotorEV3_Large, PIDControl, encoder)
#pragma config(Motor,  motorC,          right,         tmotorEV3_Large, PIDControl, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

task main()
{
	int mode = 1;
	int speed = 15;
	int speed2 = 5;

	if(mode == 1)
	{
		speed = speed + 10;
		speed2 = speed2 + 10;
		if(getMotorEncoder(right)== 18000)
		{
			mode = 2;

		}
		if (mode == 2)
		{while(true)
			{
				if(SensorValue(Rbat) <= 6)
				{
					motor[left]=47;
					motor[right]=50;
					wait1Msec(1000);
					{
						(SensorValue(Rbat) >= 7);
						{
							motor[left]=50;
							motor[right]=47;
							wait10Msec(.2);
						}
					}
				}
			}
		}
	}
}
